INSERT INTO news (id, title, teaser, body, created_at, updated_at, published_at) VALUES (71, 'Article: Crackpot Executive Beliefs 1 by John Kricfalusi', 'The creator of The Ren &amp; Stimpy Show gives people <a href="http://johnkstuff.blogspot.com/2007/01/crackpot-executive-beliefs-1.html">a peek inside the animation industry</a>.', 'The creator of The Ren &amp; Stimpy Show gives people a peek inside the animation industry.&nbsp; Don''t forget to read the comments by other animators.&nbsp; This explains why I can''t find any decent afternoon programming.&nbsp; It might make you feel a lot better about your own job.<br /><br /><a href="http://johnkstuff.blogspot.com/2007/01/crackpot-executive-beliefs-1.html"> Crackpot Executive Beliefs 1</a> by John Kricfalusi.', '2007-02-06 07:02:00', '2007-03-27 09:32:37', '2007-02-09 02:00:00');
INSERT INTO news (id, title, teaser, body, created_at, updated_at, published_at) VALUES (72, 'Book: June 29, 1999', '<img class="left" alt="Preview of June 29, 1999." src="http://surferhearts.s3.amazonaws.com/heart_links/previews/book-june_29_1999.jpg" align="left" />This is an old favorite.&nbsp; <a href="http://www.amazon.com/June-29-1999-David-Wiesner/dp/0395727677/">Great book</a> for the young reader or if you just like cool pictures.', '<img src="http://surferhearts.s3.amazonaws.com/heart_links/book-june_29_1999.jpg" alt="Front cover of June 29, 1999 by David Wiesner." class="right" align="right" />This is an old favorite.&nbsp; I first heard about it on <a href="http://www.pbskids.org/readingrainbow/books/episode100.html">Reading Rainbow</a>.&nbsp; David Wiesner tells a story through pictures of fruits and vegetables dropping down from the sky.&nbsp; Of course they are all the size of skyscrapers.&nbsp; <br /><ul><li>Cucumbers circle Kalamazoo.&nbsp; <br /></li><li>Lima beans loom over Levittown.</li></ul>  No one gets seriously hurt and it has a pretty funny ending.&nbsp; For $6, pick up a copy for your child @ <a href="http://www.amazon.com/June-29-1999-David-Wiesner/dp/0395727677/">Amazon.com</a>.&nbsp; You can also see more pictures @ <a href="http://www.houghtonmifflinbooks.com/authors/wiesner/books/books_june.shtml">the publisher''s website</a>.<br /> <img class="left" alt="Excerpt from June 29, 1999 by David Wiesner." src="http://surferhearts.s3.amazonaws.com/heart_links/book-june_29_1999-2.jpg" align="left" />', '2007-02-06 07:30:55', '2007-03-27 09:32:37', '2007-02-12 14:00:00');
INSERT INTO news (id, title, teaser, body, created_at, updated_at, published_at) VALUES (73, 'Article: Pet Project (Small Business + Macs)', '<a href="http://www.trixieandpeanut.com/">Trixie + Peanut,</a> a designer pet products company, is featured on <a href="http://www.apple.com/business/profiles/trixie/">Apple''s Small Business Profiles</a>.&nbsp;', 'I just found out <a href="http://www.trixieandpeanut.com/">Trixie + Peanut,</a> a designer pet products company, is featured on Apple''s Small Business Profiles.&nbsp; It''s fun learning about Macs in small business.&nbsp; You also get an inside peek at a cool business with style.<br /><br /><a href="http://www.apple.com/business/profiles/trixie/">Go read more about the business side of Trixie + Peanut, Inc.</a><br /><br /><em><strong>( </strong></em>By the way, I''m on a Windows XP laptop. It could be worse.&nbsp; I could be using Vista.&nbsp; <em><strong>:(</strong></em>', '2007-02-09 03:51:03', '2007-03-27 09:32:37', '2007-02-10 03:00:00');
INSERT INTO news (id, title, teaser, body, created_at, updated_at, published_at) VALUES (74, 'Pet Links for Tues. - Feb. 13, 2007', 'Stay tune for more info. on <a href="http://www.modernpooch.com/">Modern Pooch</a>, <a href="http://www.sportyk9.com/">Sporty K9</a>, <a href="http://couturepup.blogsome.com/">Couture Pup</a>,  and <a href="http://chorizodog.blogspot.com/">Chorizo: A Dog with a Blog</a>.', '<ul><li><a href="http://www.modernpooch.com/">Modern Pooch</a> - think of it as GQ for dogs.&nbsp; Plenty of funny photos and videos.&nbsp; A few are a bit scary though. <br /></li><li><a href="http://www.sportyk9.com/">Sporty K9</a> - Combine your love for sports with your love of pets with this&nbsp; officially licensed sports apparel site for canines.</li><li><a href="http://couturepup.blogsome.com/">Couture Pup</a> - A blog full of photos about celebrities and their pets.<br /></li><li><a href="http://chorizodog.blogspot.com/">Chorizo: A Dog with a Blog</a> - a JR Terrier living in India with his own blog.&nbsp; Plenty of photos!</li></ul>', '2007-02-09 04:37:53', '2007-03-27 09:32:37', '2007-02-13 18:00:00');
